"use client";

import Image from "next/image";
import Link from "next/link";
import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";

interface Product {
  _id: string;
  name: string;
  description: string;
  type: "gamepass" | "instant";
  robuxAmount: number;
  price: number;
  discountPercentage?: number;
  isActive: boolean;
  category: "robux_5_hari" | "robux_instant";
  createdAt: string;
  updatedAt: string;
}

const RobuxInstan: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [robux, setRobux] = useState(0);
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const router = useRouter();

  // Fetch products from database
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("/api/products?category=robux_instant");
        if (response.ok) {
          const data = await response.json();
          // Sort products by robuxAmount ascending
          const sortedProducts = (data.products || []).sort(
            (a: Product, b: Product) => a.robuxAmount - b.robuxAmount
          );
          setProducts(sortedProducts);

          // Set default values from first product
          if (sortedProducts && sortedProducts.length > 0) {
            setRobux(sortedProducts[0].robuxAmount);
            setSelectedProduct(sortedProducts[0]);
          }
        } else {
          console.error("Failed to fetch products");
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Format currency
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("id-ID", {
      style: "currency",
      currency: "IDR",
      minimumFractionDigits: 0,
    }).format(amount);
  };

  // Get final price with discount
  const getFinalPrice = (product: Product) => {
    if (product.discountPercentage) {
      return product.price * (1 - product.discountPercentage / 100);
    }
    return product.price;
  };

  // Check if all required fields are filled
  const isFormValid =
    selectedProduct !== null &&
    username.trim() !== "" &&
    password.trim() !== "";

  const handlePurchase = () => {
    if (!isFormValid || !selectedProduct) return;

    const price = getFinalPrice(selectedProduct);

    // Redirect to new checkout system
    const checkoutData = {
      serviceType: "robux",
      serviceId: selectedProduct._id,
      serviceName: selectedProduct.name,
      serviceImage: "", // Add image if available
      quantity: 1,
      unitPrice: price,
      robloxUsername: username,
      robloxPassword: password,
      serviceCategory: "robux_instant",
      robuxAmount: selectedProduct.robuxAmount,
    };

    // Store in sessionStorage for checkout page
    sessionStorage.setItem("checkoutData", JSON.stringify(checkoutData));
    router.push("/checkout");
  };

  const handleProductSelect = (product: Product) => {
    setSelectedProduct(product);
    setRobux(product.robuxAmount);
  };

  if (loading) {
    return (
      <div className="min-h-screen flex justify-center items-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    );
  }
  return (
    <main className="px-4 sm:px-6 md:px-8">
      <div className="max-w-6xl mx-auto px-2">
        <div className="w-full h-[200px] sm:h-[250px] md:h-[300px] relative rounded-lg overflow-hidden">
          <Image
            src="/banner2.png"
            alt="banner"
            fill
            className="object-cover"
          />
        </div>
      </div>

      <section className="max-w-6xl mx-auto bg-[#e28686] rounded-lg p-3 sm:p-4 mt-4 sm:mt-6 z-10 relative flex flex-col sm:flex-row gap-3 sm:gap-4 shadow-lg">
        <div className="flex-shrink-0 self-center sm:self-start">
          <Image
            src="/gamepass1.png"
            alt="Blox Fruits"
            width={120}
            height={120}
            className="sm:w-[150px] sm:h-[150px] rounded-md object-cover mx-auto sm:mx-0"
          />
        </div>

        <div className="flex flex-col justify-between w-full text-center sm:text-left">
          <div className="mt-2 sm:mt-5">
            <h1 className="text-lg sm:text-xl font-bold text-black leading-tight">
              Blox Fruits
            </h1>
            <p className="text-sm text-black/80">
              Gamer Robot Inc <span className="text-blue-600">‚úîÔ∏è</span>
            </p>
          </div>

          <div className="flex flex-wrap justify-center sm:justify-start gap-x-3 sm:gap-x-5 gap-y-2 text-xs sm:text-sm font-medium text-black/80 mt-3 sm:mt-4">
            <span className="flex items-center gap-1">
              <span className="text-yellow-500">‚ö°</span> Pengiriman Cepat
            </span>
            <span className="flex items-center gap-1">
              <span className="text-black">üïí</span> Layanan 24 Jam
            </span>
            <span className="flex items-center gap-1">
              <span className="text-blue-500">‚úÖ</span> Pembayaran Aman!
            </span>
          </div>
        </div>
      </section>

      <section className="max-w-6xl mx-auto mt-4 sm:mt-6 grid grid-cols-1 lg:grid-cols-4 gap-4">
        <div className="bg-[#e28686] rounded-xl px-4 sm:px-6 py-4 w-full lg:w-[230px] h-auto lg:h-[260px] flex flex-col justify-start mx-auto lg:mx-0">
          <h2 className="text-black font-extrabold text-base sm:text-lg mb-2 text-center lg:text-left">
            Cara Joki :
          </h2>
          <ol className="list-decimal list-inside text-black text-sm font-medium space-y-1 text-center lg:text-left">
            <li>tes</li>
            <li>tes</li>
            <li>tes</li>
            <li>tes</li>
            <li>tes</li>
          </ol>
        </div>

        <div className="lg:col-span-3 space-y-4">
          <div className="bg-[#e28686] rounded p-3 sm:p-4 space-y-4 w-full  mx-auto lg:mx-0">
            <div>
              <label className="text-sm font-bold mb-1 block text-black">
                Username
              </label>
              <div className="flex items-center border border-black rounded overflow-hidden bg-white w-full max-w-[520px] mx-auto lg:mx-0">
                <div className="px-3 py-2 border-r border-black bg-[#d06565] flex items-center justify-center">
                  <Image src="/src.png" alt="search" width={20} height={20} />
                </div>

                <input
                  type="text"
                  placeholder="Masukkan Username Roblox"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className={`py-2 px-3 outline-none text-sm text-black flex-1 min-w-0 transition-all ${
                    username ? "bg-[#e8f5e8]" : "bg-white"
                  }`}
                />
              </div>
            </div>
            <div>
              <label className="text-sm font-bold mb-1 block text-black">
                password
              </label>
              <div className="flex items-center border border-black rounded overflow-hidden bg-white w-full max-w-[520px] mx-auto lg:mx-0">
                <div className="px-3 py-2 border-r border-black bg-[#d06565] flex items-center justify-center">
                  <svg
                    width="20"
                    height="20"
                    viewBox="0 0 28 28"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                    xmlnsXlink="http://www.w3.org/1999/xlink"
                  >
                    <rect
                      width="28"
                      height="28"
                      fill="url(#pattern0_349_347)"
                    />
                    <defs>
                      <pattern
                        id="pattern0_349_347"
                        patternContentUnits="objectBoundingBox"
                        width="1"
                        height="1"
                      >
                        <use
                          xlinkHref="#image0_349_347"
                          transform="scale(0.00168634)"
                        />
                      </pattern>
                      <image
                        id="image0_349_347"
                        width="593"
                        height="593"
                        preserveAspectRatio="none"
                        xlinkHref="data:image/png;base64,"
                      />
                    </defs>
                  </svg>
                </div>

                <input
                  type="password"
                  placeholder="Masukkan Password Roblox"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className={`py-2 px-3 outline-none text-sm text-black flex-1 min-w-0 transition-all ${
                    password ? "bg-[#e8f5e8]" : "bg-white"
                  }`}
                />
              </div>
            </div>
          </div>

          <div className="bg-[#e28686] rounded-xl p-4 sm:p-5">
            <h2 className="font-extrabold text-base mb-4 text-black text-center lg:text-left">
              Pilih Jumlah Robux:
            </h2>
            <div className="grid grid-cols-2 sm:grid-cols-2 lg:grid-cols-4 gap-3 justify-center max-w-[740px] mx-auto">
              {products.map((product, i) => (
                <button
                  key={product._id}
                  onClick={() => handleProductSelect(product)}
                  className={`w-full  border-2 rounded-2xl px-3 py-2 flex flex-col justify-center transform transition duration-300 hover:scale-105 active:scale-95 ${
                    selectedProduct?._id === product._id
                      ? "bg-[#FF9C01] border-[#FF9C01] shadow-lg"
                      : "bg-[#f8b8b8] border-[#CE3535]"
                  }`}
                >
                  <div className="flex items-center gap-2 text-gray-700 text-xs mb-[2px] justify-center">
                    <img
                      src="/money.png"
                      alt="money icon"
                      className="w-5 h-5 sm:w-6 sm:h-6"
                    />
                    <span>{product.robuxAmount} R$</span>
                  </div>
                  <div className="text-black text-sm font-extrabold leading-tight">
                    {formatCurrency(getFinalPrice(product))}
                    {product.discountPercentage && (
                      <div className="text-xs text-gray-500 line-through">
                        {formatCurrency(product.price)}
                      </div>
                    )}
                  </div>
                </button>
              ))}
            </div>
          </div>

          <div className="text-center mt-4">
            <button
              onClick={handlePurchase}
              disabled={!isFormValid}
              className={`font-bold py-3 px-6 rounded-xl w-full max-w-[300px] sm:max-w-[400px] lg:max-w-[750px] mx-auto flex items-center justify-center gap-2 transition-all duration-300 ease-in-out transform shadow-md ${
                isFormValid
                  ? "bg-[#CE3535] text-white hover:scale-105 active:scale-95 hover:shadow-lg cursor-pointer"
                  : "bg-gray-400 text-gray-600 cursor-not-allowed opacity-50"
              }`}
            >
              Beli Sekarang
              <Image
                src="/beli.png"
                alt="cart"
                width={18}
                height={18}
                className="sm:w-5 sm:h-5"
              />
            </button>
          </div>
        </div>
      </section>
    </main>
  );
};

export default RobuxInstan;
